import { useState } from "react";


export function totalBooks(){
    const [book, setBook] = useState(13)

    const handler = (value)=>{
        setBook(book+value)
    }

    return <div className="items">
    <span>Books:</span>
    <button className="addBook" onClick={()=>{handler(1)}}>
        +
    </button>
    <button className="remBook" onClick={()=>{handler(-1)}}>
        -
    </button>
    <span className="totalBooks">{book}</span>
</div>
}
